$(function(){$("#slider-range").slider({range:!0,min:0,max:1e6,values:[$("#min_price").val(),$("#max_price").val()],slide:function(r,e){$("#amount").val(e.values[0]+" - "+e.values[1])}}),$("#amount").val($("#slider-range").slider("values",0)+" - "+$("#slider-range").slider("values",1)),$(".delivery-address").change(function(){$(".courier-code").removeAttr("checked"),$(".available-services").hide()}),$(".courier-code").click(function(){let r=$(this).val(),e=$(".delivery-address:checked").val();$.ajax({url:"/orders/shipping-fee",method:"POST",data:{address_id:e,courier:r,_token:$('meta[name="csrf-token"]').attr("content")},success:function(a){$(".available-services").show(),$(".available-services").html(a)},error:function(a){console.log(a)}})})});
